mapper.select:
SELECT

FROM activity
WHERE start_time >= #{startDate,jdbcType=TIMESTAMP}
AND end_time <= #{endDate,jdbcType=TIMESTAMP}
ORDER BY start_time DESC

mapper.update:
UPDATE activity
title = #{title,jdbcType=VARCHAR}, description = #{description,jdbcType=VARCHAR}, type = #{type,jdbcType=VARCHAR}, location = #{location,jdbcType=VARCHAR}, start_time = #{startTime,jdbcType=TIMESTAMP}, end_time = #{endTime,jdbcType=TIMESTAMP}, registration_deadline = #{registrationDeadline,jdbcType=TIMESTAMP}, organizer_id = #{organizerId,jdbcType=BIGINT}, organizer_name = #{organizerName,jdbcType=VARCHAR}, publisher_id = #{publisherId,jdbcType=BIGINT}, contact = #{contact,jdbcType=VARCHAR}, poster_url = #{posterUrl,jdbcType=VARCHAR}, max_participants = #{maxParticipants,jdbcType=INTEGER}, current_participants = #{currentParticipants,jdbcType=INTEGER}, status = #{status,jdbcType=VARCHAR}, update_time = NOW()
WHERE id = #{id,jdbcType=BIGINT}

