mapper.select:
SELECT COUNT(*)
FROM activity
WHERE type = #{type,jdbcType=VARCHAR}

SELECT COUNT(*)
FROM activity
WHERE status = #{status,jdbcType=VARCHAR}

SELECT COUNT(*)
FROM activity_participant
WHERE activity_id = #{activityId,jdbcType=BIGINT}

SELECT COUNT(*) > 0
FROM activity
WHERE title = #{name,jdbcType=VARCHAR}
