mapper.select:
SELECT

FROM post p
LEFT JOIN user u ON p.user_id = u.id


        AND p.status = #{status,jdbcType=TINYINT}


ORDER BY p.creation_time DESC

    limit #{offset,jdbcType=INTEGER}, #{limit,jdbcType=INTEGER}

SELECT COUNT(*)
FROM post p


        AND p.status = #{status,jdbcType=TINYINT}

SELECT

FROM post p
LEFT JOIN user u ON p.user_id = u.id
WHERE p.user_id = #{authorId,jdbcType=BIGINT}

    AND p.status = #{status,jdbcType=TINYINT}

ORDER BY p.creation_time DESC

    limit #{offset,jdbcType=INTEGER}, #{limit,jdbcType=INTEGER}

SELECT
FROM post p LEFT JOIN user u ON p.user_id = u.id
AND ( p.title LIKE CONCAT('%', #{keyword,jdbcType=VARCHAR}, '%') OR p.content LIKE CONCAT('%', #{keyword,jdbcType=VARCHAR}, '%') OR u.username LIKE CONCAT('%', #{keyword,jdbcType=VARCHAR}, '%') )
AND p.status = #{status,jdbcType=TINYINT}
ORDER BY p.creation_time DESC
limit #{offset,jdbcType=INTEGER}, #{limit,jdbcType=INTEGER}

mapper.update:
UPDATE post
title = #{title,jdbcType=VARCHAR}, content = #{content,jdbcType=LONGVARCHAR}, status = #{status,jdbcType=TINYINT}, is_top = #{isTop,jdbcType=TINYINT}, is_essence = #{isEssence,jdbcType=TINYINT}, last_update_time = NOW()
WHERE id = #{id,jdbcType=BIGINT}

UPDATE post
SET view_count = view_count + 1
WHERE id = #{id,jdbcType=BIGINT}
