mapper.select:
SELECT

FROM post p
LEFT JOIN user u ON p.user_id = u.id


        AND p.status = #{status,jdbcType=TINYINT}


        AND p.category = #{category,jdbcType=VARCHAR}


ORDER BY p.creation_time DESC

    limit #{offset,jdbcType=INTEGER}, #{limit,jdbcType=INTEGER}

SELECT COUNT(*)
FROM post p


        AND p.status = #{status,jdbcType=TINYINT}


        AND p.category = #{category,jdbcType=VARCHAR}


        AND JSON_CONTAINS(p.tags, JSON_ARRAY(#{tag}))

SELECT

FROM post p
LEFT JOIN user u ON p.user_id = u.id
WHERE p.user_id = #{authorId,jdbcType=BIGINT}

    AND p.status = #{status,jdbcType=TINYINT}

ORDER BY p.creation_time DESC

    limit #{offset,jdbcType=INTEGER}, #{limit,jdbcType=INTEGER}

SELECT

FROM post p
LEFT JOIN user u ON p.user_id = u.id
WHERE p.category = #{forumType,jdbcType=VARCHAR}

    AND p.status = #{status,jdbcType=TINYINT}

ORDER BY p.creation_time DESC

SELECT
FROM post p LEFT JOIN user u ON p.user_id = u.id WHERE JSON_CONTAINS(p.tags, JSON_ARRAY(#{tag}))
AND p.status = #{status,jdbcType=TINYINT}
ORDER BY p.creation_time DESC
limit #{offset,jdbcType=INTEGER}, #{limit,jdbcType=INTEGER}

SELECT

FROM post p
LEFT JOIN user u ON p.user_id = u.id


        AND (
            p.title LIKE CONCAT('%', #{keyword,jdbcType=VARCHAR}, '%')
            OR p.content LIKE CONCAT('%', #{keyword,jdbcType=VARCHAR}, '%')
        OR u.username LIKE CONCAT('%', #{keyword,jdbcType=VARCHAR}, '%')
        )


        AND p.status = #{status,jdbcType=TINYINT}


        AND p.category = #{category,jdbcType=VARCHAR}


        AND JSON_CONTAINS(p.tags, JSON_ARRAY(#{tag}))


ORDER BY p.creation_time DESC

    limit #{offset,jdbcType=INTEGER}, #{limit,jdbcType=INTEGER}

mapper.update:
UPDATE post
SET view_count = view_count + 1
WHERE id = #{id,jdbcType=BIGINT}

mapper.select:
SELECT *
FROM post
WHERE category = #{category}
ORDER BY creation_time DESC
LIMIT #{offset}, #{limit}

SELECT count(*)
FROM post
WHERE category = #{category}
